mapper.select:
SELECT au.*, pda.description AS pda_description, pu.user_name AS pda_user_name, CASE WHEN au.success_flag =0 THEN '成功' ELSE '失败' END AS success_flag_str FROM lock_authorization_log au inner JOIN lock_pda_user pu ON pu.id = au.pda_user_id inner JOIN lock_pda_info pda ON pda.id = pu.pda_id
AND au.success_flag =#{successFlag}
AND pu.user_name like CONCAT('%',#{userName},'%')
AND au.create_time >= #{queryStartDate}
AND au.create_time <= #{queryEndDate}
order by au.create_time desc
