mapper.select:
select t.* from lock_port_info t where t.del_flag = '0'
AND t.equipment_id =#{equipmentId}
order by t.create_time desc

select count(*)
from lock_port_info
where del_flag = '0';

select count(*)
from lock_port_info
where del_flag = '0'
  and deployment_status = 1;
and user_code != '';

select * from lock_port_info where equipment_id in

    #{id}

and del_flag = '0'
