mapper.select:
SELECT SUM(id) as warnInfoTotal
FROM lock_warn_info
where create_time  >=  #{startDate}
  and create_time  <=  #{endDate}
GROUP BY MONTH (create_time);

SELECT unlog.*, CASE WHEN unlog.status =1 THEN '成功' ELSE '失败' END AS status_str, pda.description as pda_description, pu.user_name as pda_user_name, equ.name AS equipment_name, pi.serial_number AS port_Index FROM lock_unlock_log unlog inner JOIN lock_pda_user pu ON pu.id = unlog.pda_user_id inner JOIN lock_pda_info pda ON pda.id = pu.pda_id left JOIN lock_port_info pi ON pi.id = unlog.port_id left JOIN lock_equipment equ ON pi.equipment_id = equ.id left join lock_company company ON pda.company_id = company.id
AND ${paramMap.companyScope}
AND unlog.status =#{status}
AND unlog.pda_user_id =#{pdaUserId}
AND pu.user_name like CONCAT('%',#{userName},'%')
AND pi.user_code like CONCAT('%',#{lockSerialNumber},'%')
AND unlog.create_time >= #{queryStartDate}
AND unlog.create_time <= #{queryEndDate}
order by unlog.create_time desc
